import React, { Component } from 'react';
import '../App.css';
import MyNav from './MyNav'
import SearchBar from '../Components/SearchBar'
import RestaurantList from './RestaurantList'
import RestaurantInfo from '../Components/RestaurantInfo'
const searchURL = 'http://localhost:4000/api/v1/restaurants?searchtype=textsearch&query='
const detailsURL = ''

class App extends Component {

  state = {
    restaurant: []
  }
  onSearchClick(queryString) {
    const uriEncode =  encodeURIComponent(queryString)
    fetch(searchURL + uriEncode)
  }
  componentDidMount(){
    fetch(URL)
    .then(res => res.json())
    .then(restaurantData => {
      console.log(restaurantData)
      this.setState({
        restaurant: restaurantData
      })
    })
  }

  render() {
    return (
      <div>
        <MyNav />
        <SearchBar />
        {this.state.restaurant ?
          <RestaurantList
            allRestaurants={this.state.restaurant}
          />
        : null}
        <RestaurantInfo />
      </div>
    )
  }
}

export default App;
